import React,{Component} from 'react';
import axios from 'axios';
import FormData from 'form-data';

import Config from "../Config/Config";



export default class Card extends Component{
	constructor(props) {
        super(props);

        this.state = {
            product_list: [],
            sub_total : 0,
            title : '',
            content : '',
            excerpt : '',
            email : '',
            tel : '',
            showErrorMessage : false,
            successMessage : '',
            showSuccessMessage : false,
        };
    }



	componentDidMount = async () => {
		
		const s_storage=window.localStorage,
		array_name=s_storage.getItem('user_cart');


	if(array_name){
		const product_ids_=[];
		const ids_data=JSON.parse(array_name);

		for(let key in ids_data){
			product_ids_.push( key );
		}


		const product_ids=product_ids_.join(',');


		const data = new FormData();
		data.append('get_basket', 'true');
		data.append('ids', product_ids);

		const config2 = {
			method: 'post',
			url: Config().ApiUrl,
			headers: {},
			data : data
		};

		const product_list = await axios(config2);

		const products = [];

		var sub_total=0;

		for (let key in product_list.data.data){
			let row=product_list.data.data[key];

			let discount=row.metas.discount,
				price=row.metas.price,
				discount_start_date=new Date(row.metas.discount_start_date),
				discount_end_date=new Date(row.metas.discount_end_date),
				now_date=new Date(),
				calc_discount=(price * (100 - discount)) / 100;

			let has_discount=(discount_start_date <= now_date && discount_end_date >= now_date) ? true : false;
			let new_price=(has_discount) ? calc_discount : price;

			let qty = ids_data[row.id];

			products.push({
				id : row.id,
				title : row.title,
				featured : row.featured,
				link : "/products/"+row.slug,
				has_discount : has_discount,
				discount : discount,
				price : price,
				new_price : new_price,
				fav_page : true,
				qty : qty
			});

			sub_total += new_price * qty;

		}

		this.setState({
			product_list : products,
			sub_total : sub_total
		});


	}
}; 

	

    render(){
        return(
            <section class="full_w card_page">
		<div class="full_w card_area">
			<div class="center">
				<div class="full_w card_contain">
					<table>
						<thead>
							<tr>
								<th class="card-head">Products</th>
								<th>Price</th>
								<th>Quantity</th>
								<th>Total</th>
								<th></th>
							</tr>
						</thead>
						<tbody>
							<tr>
								<td class="card_item">
									<img src="image/blog1.png"  alt="photo" />
									<h5>Product One</h5>
								</td>
								<td class="card-price">$12</td>
								<td class="card-quantity">
									<input type="number" name="number" min="1" max="100" value="1" />
								</td>
								<td class="card-total">
									110$
								</td>
								<td class="card-close">
									<span>&times;</span>
								</td>
							</tr>
							<tr>
								<td  class="card_item">
									<img src="image/blog2.png" alt="photo" />
									<h5>Product Two</h5>
								</td>

								<td class="card-price">$12</td>

								<td class="card-quantity">
									<input type="number" name="number" min="1" max="100" value="1" />
								</td>

								<td class="card-total">
									110$
								</td>

								<td class="card-close">
									<span>&times;</span>
								</td>
							</tr>
							<tr>
								<td  class="card_item">
									<img src="image/blog3.png"  alt="photo" />
									<h5>Product Three</h5>
								</td>
								<td class="card-price">$12</td>
								<td class="card-quantity">
									<input type="number" name="number" min="1" max="100" value="1" />
								</td>
								<td class="card-total">
									110$
								</td>
								<td class="card-close">
									<span>&times;</span>
								</td>
							</tr>
						</tbody>
					</table>

						<div class="card_btn">
							<a href="#" class="card_b card_shop">CONTINUE SHOPPING</a>
							<a href="#" class="card_b card_update"><i class="fas fa-spinner"></i>  UPDATE CARD </a>
						</div>

						<div class="card_checkout">
			              <h5>Card Total</h5>
			              
							 <ul>
								<li>Subtotal <span>$300</span> </li>
								<li>Total <span>$300</span> </li>
							</ul> 
			                
							<a href="#" class="procced_b">PROCEED TO CHECKOUT</a>
						</div>
					
				</div>
			</div>
		</div>
	</section>
        )
    }

}
