import React,{ Component } from "react";
import axios from 'axios';



export default class Blog extends Component{
     constructor(props){
         super(props);

         this.state = {
             blog_post1 :[],
             news_list : [],
             translate_list : []
         };
     }
     
     static defaultProps = {
        apiUrl : 'http://localhost/my-app1/'  
    };

    componentDidMount = async() =>{
        const FormData = require('form-data');
        const data = new FormData();
        data.append('get_post', true);
        data.append('post_id', 83);
        
        const config = {
          method: 'post',
          url: this.props.apiUrl,
          headers: {},
          data : data
        };
        
        const make_post = await axios(config);

        this.setState({
          blog_post1 : make_post.data
        });



          /** */
          
      const data1 = new FormData();
      data1.append('get_translate', true);

      const config1 = {
        method: 'post',
        url: this.props.apiUrl,
        headers: {},
        data : data1
      };

      const translate_list = await axios(config1);

      this.setState({
        translate_list : translate_list.data
      });

      const month = translate_list.data.month_list.split(', ');



        /** */
        const data2 = new FormData();
        data2.append('get_posts', true);
        data2.append('post_type', 8);

      const config2 = {
        method: 'post',
        url: this.props.apiUrl,
        headers: {},
        data : data2
      };

      const news_list = await axios(config2);
      const make_news = [];

      for(let key in news_list.data.data){
        let row = news_list.data.data[key];
        let date = row.date.split(' ');
        let date_2 =date[0].split('-');

        let month_index =parseInt(date_2[1] - 1);


        make_news.push({
          id : row.id,
          title : row.title,
          featured : row.featured,
          excerpt : row.excerpt,
          date : date_2[2] + ' ' + month[month_index] + ' ' + date_2[0],
          link : '/blog/'+ row.slug
        });

        this.setState({
          news_list : make_news
        });
    }

  

  }


    render(){
        const{blog_post1,news_list}=this.state;
        return(
            <section class="full_w">
            <div class=" full_w blog_area">
        <div class="center">
                    <div class="blog_main full_w">
                  <div class="read_our_blog2">
          <div class="texts">
            <h1>{blog_post1.title}</h1>
            <p>{blog_post1.excerpt}</p>
          </div>
        <div class="menu_row" id="boxs">
         {
           news_list.map(news =>{
             return(
              <div class="blog_box box" key={news.id}>
            <a href="#">
            <img src={news.featured} alt={news.title} title={news.title}/>
            </a>
              <div class="blog_text">
               <h2>{news.title}</h2>
               <h6>{news.date}</h6>
              <p class="menu_col_text">
                {news.excerpt}
              </p>
              <a href="organic" target="_blank" class="lnk">Read More</a>
              </div>
              
              </div>
             )
           })
         }
      </div>
            <div class="blog_btn">
             <button class="button" type="button" id="showMore">Load More</button>
              
            </div>
    
            </div>
            </div>
    
               
                </div>
                
            </div>
    
            
        </section>
        )
    }
}
