import React,{Component} from 'react';
import axios from 'axios';


export default class Blog extends Component{
    constructor(props){
      super(props);

      this.state= {
        post_blog : [],
        post_slug : this.props.match.params.slug,
        translate_list : [],
        latest_blog : []
      };
    }

    static defaultProps = {
      apiUrl : 'http://localhost/my-app1/'  
  };

    componentDidMount = async () =>{

      /* TRANSLATE LIST MONTH LIST */

      const data = new FormData();
      data.append('get_translate', 'true');
     

      const config = {
      method: 'post',
      url: this.props.apiUrl,
      headers:{},
      data : data
      };

     const translate_list = await axios(config);

    this.setState({
      translate_list : translate_list.data
    });

    const month = translate_list.data.months.split(', ');

    console.log(translate_list.data.months);


    /* POST INFO & DATE */

      const data1 = new FormData();
      data1.append('get_post_by_slug', true);
      data1.append('slug', this.state.post_slug);

      const config1 = {
        method: 'post',
        url: this.props.apiUrl,
        headers: {},
        data : data1
      };

      const post_box = await axios(config1);

      this.setState({
          post_blog : post_box.data
      });

      const date=post_box.data.date.split(' ');
      const date_2=date[0].split('-');
      const month_index=parseInt(date_2[1] - 1);
      
      this.setState({
        date : date_2[2] + ' ' + month[month_index] + ' ' + date_2[0]
      });



      /* RELATED POSTS */
      const data2 = new FormData();
      data2.append('get_posts', true);
      data2.append('post_type', 8);

      const config2 = {
        method: 'post',
        url: this.props.apiUrl,
        headers: {},
        data : data2
      };

      const blog_box = await axios(config2);
      const makeBox = [];

      for(let key in blog_box.data.data){
        let row = blog_box.data.data[key];


        makeBox.push({
          id : row.id,
          title : row.title,
          link :'/blog/'+ row.slug
        });

        this.setState({
          latest_blog : makeBox
        });
      }


    }

   
        render(){

          const {post_blog,post_slug,date,latest_blog}=this.state;
        console.log(post_blog);
            return(
                <section className="full_w">
