import React,{ Component } from "react";
import axios from 'axios';
import FormData from 'form-data';
import ProductLi from "../General/ProductLi";
import Config from '../Config/Config';


export default class Product extends Component{
  constructor(props){
    super(props);


    this.state = {
      product_data : [],
      prdct_data : []
  
    }
  }


  componentDidMount = async () =>{
    const data = new FormData();
    data.append('get_posts', true);
    data.append('post_type', 10);

    const config = {
      method: 'post',
      url: Config().ApiUrl,
      headers: {},
      data : data
    };

    const infos = await axios(config);
    const makePrdct = [];


 

    for(let key in infos.data.data){
      let row = infos.data.data[key];
      

      let discount = row.metas.discount,
         price = row.metas.price,
         discount_start_date = new Date(row.metas.discount_start_date),
         discount_end_date = new Date(row.metas.discount_end_date),
         now_date = new Date(),
         calc_discount = (price * (100 - discount)) / 100;

         let has_discount = (discount_start_date <= now_date && discount_end_date >= now_date) ? true : false;
         let new_price = (has_discount) ? calc_discount : null



      makePrdct.push({
        id : row.id,
        title : row.title,
        featured : row.featured,
        link : '/products/'+row.slug,
        content : row.content,
        excerpt : row.excerpt,
        price : price,
        new_price :new_price,
        discount : discount,
        has_discount : has_discount

      });

     

      this.setState({
        product_data : makePrdct
      });

        /** */


        const data1 = new FormData();
        data1.append('get_post', 'true');
        data1.append('post_id', '7');

        const config1 = {
          method: 'post',
          url: Config().ApiUrl,
          headers:{},
          data : data1
        };

        const prdct_data = await axios(config1)
        const prdctBox = prdct_data.data;


        this.setState({
          prdct_data : prdctBox
        })

    }
  }

    render(){

      const {product_data,prdct_data}=this.state;
        return(
            <section className="full_w sct_p">
            <div className=" full_w product_area">
        <div className="center">
                    <div className="blog_main full_w">
                  <div className="read_our_blog2">
          <div className="texts">
            <h1>{prdct_data.title}</h1>
            <p>{prdct_data.excerpt}</p>
          </div>
        <div className="menu_row">
         {
           product_data.map(product =>{
             return(
              <div className="blog_box prdct" key={product.id}>
              <div className="product_action">
  
                <a href='#' onClick={this.addFavorite.bind(this, product.id)} className={ ( (fav_ids && fav_ids.indexOf( product.id) !=-1) || product_favorite_status) ? 'add_wish_list active' : ' add_wish_list ' }><i className="fas fa-heart"></i></a>
  
                <a href={product.link} target="_blank" className={ ( (cart_ids &&  cart_ids[product.id]) || product_cart_status  ) ? 'add_cart active' : ' add_cart ' }  onClick={this.addCart.bind(this, product.id)} ><i className="fas fa-shopping-bag"></i></a>
  
                <a href="#" onClick={this.addCompare.bind(this, product.id)} className={ ( (compare_ids && compare_ids.indexOf( product.id) !=-1) || product_compare_status) ? 'add_compare active' : ' add_compare ' }><i className="fa fa-exchange"></i></a>
  
              </div>
           <a href={product.link} target="_blank"> <img src={product.featured} alt={product.title} title={product.title}/></a>
  
            <div className="label_product">
             {
               (product.has_discount) ?  <span className="label_sale">&#x2010;{product.discount} &#x25;</span> : null 
             }
            </div>
            <div className="blog_text">
             <h2>{product.title}</h2>
             <div className="price_list">

              {
                (product.has_discount) ? <p className="price">&#36;{product.new_price} <del>&#36;{product.price}</del></p> : <p className="price">&#36;{product.price} </p>
              }
              
             </div>
            <p className="prdct_text">
             {product.excerpt}
            </p>
            </div>
            
            </div>
             )

           })

           }
          
            
           

          
            </div>
    
            </div>
            </div>
    
               
                </div>
                
            </div>
    
            
        </section>
    
    
    
    
        )
    }
}
