import React,{Component} from "react";
import axios from "axios";
import Config from '../Config/Config';



export default class Header extends Component {
    constructor(props){
      super(props);

      this.state = {
          header_menu : []
      }
    }

  

    componentDidMount = async () =>{
      const FormData = require('form-data');
      const data = new FormData();
      data.append('get_menus', true);
      data.append('menu_type', 1);

          const config = {
          method: 'post',
          url: Config().ApiUrl,
          headers: {},
          data : data
          };

          const menu = await axios(config);
          const make_menu = [];

          for(let key in menu.data){
            let row =  menu.data[key];

            make_menu.push({
              id : row.id,
              title : row.title,
              content : row.content
            });


            this.setState({
              header_menu : make_menu
            });
          }

    }


    render(){
      const { header_menu} = this.state;
        return(
            <header class="full_w">
    <div class="full_w header_menu">
      <div class="center">
        
        <div class="navSectionWrapper full_w ">
        <a href="#" class="logo">
          <img src="/assets/image/logo-org.svg" /><span>Organic</span>
        </a>
          
        <ul class="menu">
          {
            header_menu.map(menu_item =>{
              return(
         <li key={menu_item.id}><a href={menu_item.content} class="lnk" alt={menu_item.title} title={menu_item.title}>{menu_item.title}</a></li>

              );
            })
          }
        </ul>
          <div class="mobilenav">
            <div class="l1"></div>
            <div class="l2"></div>
            <div class="l3"></div>
          </div>
    </div>
  
    
  
  </div>
      
    </div>
  </header>
        )
    }
}
