import React,{ Component } from "react";
import axios from 'axios';
import Config from "../Config/Config";
import Translate from "../Config/Translate";



export default class Contact extends Component{
      constructor(props){
        super(props);


        this.state = {
          title : '',
          content : '',
          email : '',
          lastName : '',
          phone : '',
          showErrorMessage : false,
          ShowSuccessMessage : false,
          successMessage : []
        }
      }

    


      updateState = (e) =>{
        const {value,name}=e.target;


        this.setState({
          [name] : value

        });
      };

      sendMessage = async(e) =>{
        e.preventDefault();

       const {title,content,email,lastName,phone}=this.state;

        this.setState({
          showErrorMessage : true,
          ShowSuccessMessage : false
        });


        if(title && content && email && lastName && phone){
          const data = new FormData();
          data.append('contact_form', true);
          data.append('title', title);
          data.append('title', lastName);
          data.append('meta[')
          data.append('content', content);
          data.append('meta[1]', email);
  
          const config = {
            method: 'post',
            url: Config().ApiUrl,
            headers: {},
            data : data
          };
  
          const successMessage = await axios(config);


          this.setState({
            title : '',
            content : '',
            email : '',
            lastName : '',
            phone : '',
            showErrorMessage : false,
            ShowSuccessMessage : true,
            successMessage : successMessage
             
          });
        }

        return false;
      }


    render(){

      const {ShowSuccessMessage,successMessage,showErrorMessage,title,content,phone,lastName,email}=this.state;
        return(

          
            <section class="contact_area full_w">
            <div class="contact_content full_w">
              <div class="center">
              <div class="forms">
                <div class="forms_text">
                  <h2>Send us a message</h2>
                  <p>Feel free to fill out the form and reach to us.<br/>
                      We will get back to you shortly.</p>
                </div>
        
                <form action="" onSubmit={this.sendMessage} method="post">
                  
                  <div className="input-row text">
                  <p>
                    {
                      ShowSuccessMessage ? <span>{successMessage}</span> : null
                    }
                      
                      </p>
                  </div>
                 
                  <div class="input-row">
                    <div class="input-group">
                      {
                        showErrorMessage && !title ? <span>Adinizi daxil edin</span> : null
                      }
                      <input type="text" name="title" value={title} placeholder="First Name"  onChange={this.updateState} />
                    </div>
                     <div class="input-group">
                     {
                        showErrorMessage && !lastName ? <span>Soyadinizi daxil edin</span> : null
                      }
                      <input type="text" name="lastName" value={lastName} placeholder="Last Name" onChange={this.updateState}/>
                    </div>
                    
                  </div>
                    <div class="input-row">
                    <div class="input-group">
                    {
                        showErrorMessage && !email ? <span>Soyadinizi daxil edin</span> : null
                      }
                      <input type="email" name="email" value={email} placeholder="Email Address" 
                      onChange={this.updateState} />
                    </div>
                     <div class="input-group">
                     {
                        showErrorMessage && !phone ? <span>Soyadinizi daxil edin</span> : null
                      }
                      <input type="text" name="phone" placeholder="Phone" value={phone} onChange={this.updateState} />
                    </div>
                    
                  </div>
        
                  <div class="input-row">
                  <div>
                  {
                        showErrorMessage && !content ? <span>Soyadinizi daxil edin</span> : null
                      }
                    <textarea placeholder="Message" name="content" onChange={this.updateState} value={content} ></textarea>

                  </div>
                    
                  </div>

                    
                 <div class="input-row btn-sub">
                   <input type="submit" name="" value="Submit"  class="button"  onChange={this.updateState}/>
                 </div>

               
                </form>
                
              </div>
              
            </div>
         </div>
            </section>
         
         
        );
    }
}
