import React, {Component} from 'react';
import axios from 'axios';
import FormData from 'form-data';
import Config from "../Config/Config";
import Common from "../General/Common";


export default class Favorites extends Component{

    constructor(props) {
        super(props);

        this.state = {
            product_list: []
        };
    }

    
    componentDidMount = async () => {
        const s_storage=window.localStorage,
            array_name=s_storage.getItem('favorite_ids');


        if(array_name){
            const product_ids=JSON.parse(array_name).join(',');


            const data = new FormData();
            data.append('get_basket', 'true');
            data.append('ids', product_ids);

            const config2 = {
                method: 'post',
                url: Config().ApiUrl,
                headers: {},
                data : data
            };

            const product_list = await axios(config2);

            const products = [];

            for (let key in product_list.data.data){
                let row=product_list.data.data[key];

                let discount=row.metas.discount,
                    price=row.metas.price,
                    discount_start_date=new Date(row.metas.discount_start_date),
                    discount_end_date=new Date(row.metas.discount_end_date),
                    now_date=new Date(),
                    calc_discount=(price * (100 - discount)) / 100;

                let has_discount=(discount_start_date <= now_date && discount_end_date >= now_date) ? true : false;
                let new_price=(has_discount) ? calc_discount : null;

                products.push({
                    id : row.id,
                    title : row.title,
                    featured : row.featured,
                    link : "/products/"+row.slug,
                    has_discount : has_discount,
                    discount : discount,
                    price : price,
                    new_price : new_price,
                    fav_page : true
                });
            }

            this.setState({
                product_list : products
            });


        }



    };


    render(){
        const {product_list} = this.state;
        return(
            <section class="full_w sct_p">
		<div class=" full_w product_area">
	<div class="center">
<div class="blog_main full_w" style={{height:"400px"}}>
             {
                (product_list.length ==0) ? <span>Sizin seçilmiş məhsulunuz yoxdu!</span> : null
            }
    <div class="menu_row"> 
     {
         product_list.map(product =>{
             return(

                <Common product={product} />
            //     <div class="blog_box prdct">
            //     <div class="product_action">
            //       <a href="#"><i class="fas fa-heart"></i></a>
            //       <a href="related-product.htm" target="_blank"><i class="fas fa-shopping-bag"></i></a>
            //       <a href="#"><i class="fa fa-exchange"></i></a>
            //     </div>
            //   <img src={product.featured} alt={product.title} title={product.title} />
            //    <div class="label_product">
            //     <span class="label_sale">-10%</span>
            //   </div>
            //   <div class="blog_text">
            //     <h2>Product One</h2>
            //     <div class="price_list">
            //     <p class="price">$15 <del>$20</del></p>
            //   </div>
            //   <p class="prdct_text">
            //     Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod.
            //   </p>
            // </div>
            //   </div>
             )
         })
     }
     
        </div>
        </div>
        </div>  
			</div>
	</section>


        )
    }
}
